function electron_run() {
    var electron = require("electron");
    var app = electron.app;
    app.on('ready', function () {
        var mainWindow = new electron.BrowserWindow({ width: 800, height: 600 });
        mainWindow.loadURL("file://" + __dirname + "/../test/index.html");
        setTimeout(function () { return mainWindow.webContents.openDevTools(); }, 100);
        // 启用开发工具。
        ;
    });
}
electron_run();
